<?php if (!defined('BASEPATH'))    exit('No direct script access allowed');class P extends CI_Controller{    public function __construct()    {        parent::__construct();        $this->load->model('Mod_general');        $this->load->library('dbtable');        $this->load->theme('layout');    }    public function index()    {        $get_id = $this->uri->segment(3);        $PostID = $get_id;        $data = array();        if (!empty($PostID)) {            $GetPost = $this->Mod_general->select('posts', '*', array('po_url' => $PostID,'po_type'=>'page'));            if (!empty($GetPost)) {                $data['data_post'] = $GetPost[0];                $data['title'] = $GetPost[0]->po_title;                $data['page_content'] = $GetPost[0]->po_desc;                $data['site_desc'] = $GetPost[0]->po_title;                $data['site_keyword'] = $GetPost[0]->po_keyword;                $data['site_url'] = current_url();            }        } else {            $data['data_post'] = array();            $data['title'] = 'No data...';        }        $this->load->view('p/index', $data);    }}